def compte_handle(array)
 puts "il y a #{array.length}comptes dans cette array"
 #array.length donne la longueur du tableau ( le nombre de compte)
end

def handle_short(array)
    #handle le plus court = array.min_by(&:length) 
    puts "le handle le plus cours est : #{array.min_by(&:length)} "
#array.min_by(&:length) trouve le handle(compte) le plus court en se basant sur la totalité des handle dans le array
end    

def handle_caractere(array)
 handles_5_caracteres = array.select { |handle| handle.length - 1 == 5 }
 # |handle| sert juste a changer tous les mot témporerement le temps de trouver les mot avec 5 caratères
 # tu peut aussi fair |salut| faudra juste fair salut.length - 1 == 5
  puts "Il y a #{handles_5_caracteres.length} handles contenant 5 caractères dans cette array."
end   

def handle_majuscule(array)
 #.match? = correspondre
 handles_majuscule = array.select { |handle| handle.match?(/[A-Z]/) }
 puts "Il y a #{handles_majuscule.length} handles contenant une majuscule dans cette array."
end

def handle_sort(array) #sort = trier
  handle_sort = array.sort { |a, z| a <=> z }
  puts "list alphabetique : "
  puts array.sort
end

def handle_sort_size(array)
    handle_sort_size = array.sort_by(&:length)
    puts "ICI ==> list alphabetique taille des handle : "
    puts  array.sort_by(&:length)
    
end


def order_alpha(array)
    order_alpha = array.index("@epenser")
    puts "@epenser est a la position #{order_alpha}"
end

def repartition_par_taille(array)
    for number in 1..33
     chiffre = array.select { |handle| handle.length == number }
     if chiffre.length != 0
         puts "Il y a #{chiffre.length} handles contenant #{number} caractères dans cette array."
        end
    end
end

compte_handle(array)
handle_short(array)
handle_caractere(array)
